package com.xxxman.test.select.util;

import android.util.Base64;

import java.io.ByteArrayInputStream;
import java.io.InputStream;

/**
 * Created by tuzi on 2017/10/27.
 */

public class Base64JPG {
    public static final String jpg = "";

    public static InputStream toInputStream(){
        try {
            byte[] b  = Base64.decode(jpg,Base64.DEFAULT);
            InputStream sbs = new ByteArrayInputStream(b);
            return sbs;
        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }

}
